import styled from "styled-components";

export const NewPost = styled.form`
  background: #fff;
  border: 1px solid black;
  margin: 30px 25%;
  padding: 20px;
  min-width: 375px;

  display: flex;
  flex-direction: column;
`;

export const InputFile = styled.span`
  background: #7159c1;
  color: #fff;
  padding: 3px;
  cursor: pointer;
  flex: 1;
`;

export const Input = styled.input`
  margin: 5px 0px;
  flex: 1;
`;

export const Button = styled.button`
  flex: 1;
`;
