import React, { useState } from "react";
import * as s from "./styled-new";

export default () => {
  const [image, setImage] = useState({});

  return (
    <>
      <s.NewPost>
        <s.InputFile for="file">{image.name || "Escolher Foto"}</s.InputFile>

        <s.Input type="text" name="Autor do post" />
        <s.Input type="text" name="Local do post" />
        <s.Input type="text" name="Descrição do post" />
        <s.Input type="text" name="Hashtags" />

        <s.Button>Enviar</s.Button>
      </s.NewPost>
      <input
        style={{ display: "none" }}
        onChange={({ target }) => target.files && setImage(target.files[0])}
        type="file"
        id="file"
      />
    </>
  );
};
