import React, { useState, useEffect } from "react";
import * as s from "./styled-feed";
import { icons } from "../../assets";
import { getPosts } from "../../services";
import { Loader } from "../../components";

export default ({}) => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    console.log("postsApi");
    getPosts()
      .then((postsApi) => {
        setPosts(postsApi);
        console.log(postsApi);
      })
      .catch((e) => console.log(e));
  }, []);

  if (!posts.length)
    return (
      <LoaderContainer>
        {" "}
        <Loader />
      </LoaderContainer>
    );

  return (
    <s.PostList>
      {posts.map((post) => (
        <s.PostWrapper key={post.id}>
          <s.PostHeader>
            <s.UserInfo>
              <s.UserLabel>{post.author}</s.UserLabel>
              <s.PlaceLabel>{post.place}</s.PlaceLabel>
            </s.UserInfo>

            <s.Icon src={icons.more} />
          </s.PostHeader>

          <s.PostImage src={`http://localhost:3333/file/${post.image}`} />

          <s.PostFooter>
            <s.Actions>
              <s.Icon height={20} src={icons.like} alt="like" />
              <s.Icon height={20} src={icons.comment} alt="comment" />
              <s.Icon height={20} src={icons.send} alt="send" />
            </s.Actions>
            <strong>{post.likes}</strong>

            <s.PostDescription>
              {post.description} <br />
              <s.PostHashTags>{post.hashtags}</s.PostHashTags>
            </s.PostDescription>
          </s.PostFooter>
        </s.PostWrapper>
      ))}
    </s.PostList>
  );
};
