import axios from "axios";

const api = axios.create({
  baseURL: "http://localhost:3333/",
});

export const getPosts = () => {
  return new Promise((resolve, reject) => {
    api
      .get("posts")
      .then(({ data }) => resolve(data))
      .catch((e) => reject(e));
  });
};
