import React, { useState } from "react";
import { useForm } from "react-hook-form";
import * as s from "./styled-new";

export default () => {
  const { register, handleSubmit } = useForm();

  const submit = (formData) => {
    console.log(formData);
  };

  return (
    <>
      <s.NewPost onSubmit={handleSubmit(submit)}>
        <s.InputFile for="image">{"image.name" || "Escolher Foto"}</s.InputFile>

        <s.Input name="author" ref={register({ required: true })} />
        <s.Input name="place" ref={register({ required: true })} />
        <s.Input name="description" ref={register({ required: true })} />
        <s.Input name="hashtags" ref={register({ required: true })} />

        <s.Button type="submit" value="Enviar" />

        <input
          style={{ display: "none" }}
          ref={register({ required: true })}
          type="file"
          name="image"
          id="image"
        />
      </s.NewPost>
    </>
  );
};
